import React, { useState } from "react";

import "./CounterComponent.css";
// get our fontawesome imports
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
const CounterComponent = () => {
  const [enterCount, setEnteredCount] = useState(0);

  const addCounterHandler = () => {
    setEnteredCount(enterCount + 1);
    console.log(enterCount);
  };
  const reduceCounterHandler = () => {
    setEnteredCount(enterCount - 1);
  };
  const resetCounterHandler = () => {
    setEnteredCount(0);
  };
  return (
    <div className="counter">
      <div className=".countNumber">
        <div className="counter-count">{enterCount}</div>
      </div>
      <div className="count-handler">
        <div className="btn" onClick={addCounterHandler}>
          <FontAwesomeIcon icon={faPlus} />
        </div>
        <div className="" onClick={reduceCounterHandler}>
          reduce
        </div>
        <div className="reset-control" onClick={resetCounterHandler}>
          Reset
        </div>
      </div>
    </div>
  );
};
export default CounterComponent;
